const res = [
    {
        "x": msg.payload.length === 0
            ? msg.timestamp.midnight
            : parseInt(msg.payload[0].timestamp),
        "y": 0,
    }
];

for(let idx = 1; idx < msg.payload.length; idx++) {
    const cur = parseInt(msg.payload[idx].payload);
    const bef = parseInt(msg.payload[idx - 1].payload);

    res.push({
        "x": parseInt(msg.payload[idx].timestamp),
        "y": (cur < bef ? bef - cur : 0) + res[res.length - 1]["y"],
    });
}

flow.set('waterConsumptionCache', {
    start: msg.timestamp.midnight,
    consumption: res,
});

return msg;

